      }

      // Get canvas context
      if (canvasRef_Tri.current !== null) {
      const ctx = canvasRef_Tri.current.getContext("2d");
      requestAnimationFrame(()=>{drawTri(face, ctx, draw, (draw > 0 ? false : true))});
      }
