// 1. Install dependencies DONE
// 2. Import dependencies DONE
// 3. Setup webcam and canvas DONE
// 4. Define references to those DONE
// 5. Load posenet DONE
// 6. Detect function DONE
// 7. Drawing utilities from tensorflow DONE
// 8. Draw functions DONE

// Face Mesh - https://github.com/tensorflow/tfjs-models/tree/master/facemesh

import React, { useRef, useLayoutEffect, useState } from "react";
import "./App.css";
import * as tf from "@tensorflow/tfjs";
// OLD MODEL
//import * as facemesh from "@tensorflow-models/facemesh";

// NEW MODEL
import * as facemesh from "@tensorflow-models/face-landmarks-detection";
import Webcam from "react-webcam";
import { drawMesh } from "./utilities";
import DasAlerts from "./report";
import AppBar from "./AppBar";

import Button from '@material-ui/core/Button';

function App() {
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [draw, setDraw] = useState(0);
  const [visible, setAlertVisibility] = useState(false);

  //  Load posenet
  const runFacemesh = async (key) => {
    // OLD MODEL
    // const net = await facemesh.load({
    //   inputResolution: { width: 640, height: 480 },
    //   scale: 0.8,
    // });
    // NEW MODEL
    const net = await facemesh.load(facemesh.SupportedPackages.mediapipeFacemesh);
    setInterval(() => {
      detect(net, key);
    }, 20);
  };

  const detect = async (net, key) => {
    if (
      typeof webcamRef.current !== "undefined" &&
      webcamRef.current !== null &&
      webcamRef.current.video.readyState === 4
    ) {
      // Get Video Properties
      const video = webcamRef.current.video;
      const videoWidth = webcamRef.current.video.videoWidth;
      const videoHeight = webcamRef.current.video.videoHeight;

      // Set video width
      webcamRef.current.video.width = videoWidth;
      webcamRef.current.video.height = videoHeight;

      // Set canvas width
      canvasRef.current.width = videoWidth;
      canvasRef.current.height = videoHeight;

      // Make Detections
      // OLD MODEL
      //       const face = await net.estimateFaces(video);
      // NEW MODEL
      const face = await net.estimateFaces({input:video});
      // console.log(face);

      // Get canvas context
      const ctx = canvasRef.current.getContext("2d");
      requestAnimationFrame(()=>{drawMesh(face, ctx, draw)});
    }
  };

  useLayoutEffect(()=>{runFacemesh(); return () => {}},[draw]);

  return (
    <div className="App">
      <AppBar/>
      <div>
        <button
          onClick={() => setDraw(draw + 1)}
        >
          change mesh
        </button>
      </div>
      <div>
        <button
          onClick={() => setDraw(draw - 1)}
        >
          minus mesh
        </button>
      </div>
      <div>current draw is {draw}.</div>
      <button 
        onClick={()=> setTimeout(() => {setAlertVisibility(true)},5000)}>
          refresh
      </button>
      <div>
      <DasAlerts
        visible={visible}
        duration={20000000}
        onDurationEnd={setAlertVisibility}
      >
        <div>Fix your face 1.</div>
        </DasAlerts>
      </div>
      <header className="App-header">
        <Webcam
          ref={webcamRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zindex: 9,
            width: 640,
            height: 480,
          }}
        />

        <canvas
          ref={canvasRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zindex: 9,
            width: 640,
            height: 480,
          }}
        />
      </header>
    </div>
  );
}

export default App;
